package us.hall.trz.osx;import java.awt.Color;public class LabelColor extends Color {	private String name;	static final long serialVersionUID = -3612527493952367503L;		public LabelColor(int r,int g, int b, String name) {		super(r,g,b);		this.name = name;	}		public String toString() { return name; }	public static short shortValue(String labelColor) {		if (labelColor.equals("gray")) return 1;		if (labelColor.equals("green")) return 2;		if (labelColor.equals("purple")) return 3;		if (labelColor.equals("blue")) return 4;		if (labelColor.equals("yellow")) return 5;		if (labelColor.equals("red")) return 6;		if (labelColor.equals("orange")) return 7;		return 0;	}}